package com.example.thibault.feedme.Persistence;

import com.j256.ormlite.field.DatabaseField;

import java.util.Date;

/**
 * Created by Thibault on 19/09/2015.
 */
public class Particulier {


    // id is generated by the database and set on the object automagically
    @DatabaseField(generatedId = true)
    int id;
    @DatabaseField
    String prenom;
    @DatabaseField
    Date dateNaissance;
    @DatabaseField(foreign = true)
    Integer idUser;


    Particulier() {
        // needed by ormlite
    }

    public Particulier(String prenom, Date dateNaissance, Integer idUser) {

        this.prenom = prenom;
        this.dateNaissance = dateNaissance;
        this.idUser = idUser;
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append("id=").append(this.id);
        sb.append(", ").append("Pr√©nom : ").append(this.prenom);
        sb.append(", ").append("date de naissance : ").append(this.dateNaissance);
        sb.append(", ").append("utilisateur").append(this.idUser);
        return sb.toString();
    }
}
